// ExportToWord.js
import React from 'react';
import { Document, Packer, Paragraph, Table, TableRow, TableCell } from 'docx';
import { saveAs } from 'file-saver';

const ExportToWord = ({ selectedEmployees }) => {
    const handleExport = async () => {
        const doc = new Document();

        // Пример добавления заголовков
        const title = new Paragraph("Список сотрудников");
        doc.addSection({
            properties: {},
            children: [title],
        });

        // Добавление данных сотрудников в документ
        selectedEmployees.forEach(emp => {
            const row = new TableRow({
                children: [
                    new TableCell({ children: [new Paragraph(emp.firstName)] }),
                    new TableCell({ children: [new Paragraph(emp.lastName)] }),
                    new TableCell({ children: [new Paragraph(emp.salary.toString())] }),
                    new TableCell({ children: [new Paragraph(emp.jobType)] }),
                    new TableCell({ children: [new Paragraph(emp.department)] }),
                ],
            });

            // Добавьте строку в таблицу или другую логику
        });

        // Сохранение файла
        const blob = await Packer.toBlob(doc);
        saveAs(blob, "employees.docx");
    };

    return (
        <button onClick={handleExport}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
