import React, { useEffect, useState } from 'react';
import { Document, Packer, Paragraph, TextRun } from 'docx';
import { saveAs } from 'file-saver';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const ExportToWord = ({ selectedEmployees }) => {
    const [jobTypes, setJobTypes] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [vacationStartDates, setVacationStartDates] = useState({});
    const [vacationEndDates, setVacationEndDates] = useState({});

    useEffect(() => {
        const fetchJobTypes = async () => {
            try {
                const response = await fetch('http://localhost:8000/api/job-types/');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setJobTypes(data);
            } catch (error) {
                console.error('Error fetching job types:', error);
            }
        };

        const fetchDepartments = async () => {
            try {
                const response = await fetch('http://localhost:8000/api/departments/');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setDepartments(data);
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        fetchJobTypes();
        fetchDepartments();
    }, []);

    const handleStartDateChange = (employeeId, date) => {
        setVacationStartDates(prev => ({ ...prev, [employeeId]: date }));
    };

    const handleEndDateChange = (employeeId, date) => {
        setVacationEndDates(prev => ({ ...prev, [employeeId]: date }));
    };

    const handleExport = () => {
        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const docs = selectedEmployees.map(emp => {
            const jobType = jobTypes.find(job => job.id === emp.job_type)?.name || 'Unknown Job Type';
            const department = departments.find(dept => dept.id === emp.department)?.name || 'Unknown Department';

            const vacationStart = vacationStartDates[emp.id] ? vacationStartDates[emp.id].toLocaleDateString() : 'Не указано';
            const vacationEnd = vacationEndDates[emp.id] ? vacationEndDates[emp.id].toLocaleDateString() : 'Не указано';

            const doc = new Document({
                sections: [{
                    properties: {},
                    children: [
                        new Paragraph({
                            text: `Заявление на отпуск`,
                            heading: 'Title',
                        }),
                        new Paragraph({
                            text: `Я, ${emp.first_name} ${emp.last_name}, занимающий должность "${jobType}" в отделе "${department}", прошу предоставить мне отпуск.`,
                            spacing: { after: 200 },
                        }),
                        new Paragraph({
                            text: `Дата начала отпуска: ${vacationStart}`,
                            spacing: { after: 200 },
                        }),
                        new Paragraph({
                            text: `Дата окончания отпуска: ${vacationEnd}`,
                            spacing: { after: 200 },
                        }),
                        new Paragraph({
                            text: `Подпись: ____________`,
                            spacing: { after: 200 },
                        }),
                        new Paragraph({
                            text: `Дата генерации документа: ${new Date().toLocaleDateString()}`,
                            spacing: { after: 200 },
                        }),
                    ],
                }],
            });

            return doc;
        });

        docs.forEach((doc, index) => {
            Packer.toBlob(doc).then(blob => {
                saveAs(blob, `employee_${selectedEmployees[index].first_name}_${selectedEmployees[index].last_name}_vacation_request.docx`);
            });
        });
    };

    return (
        <button className="export-button" onClick={handleExport} disabled={selectedEmployees.length === 0}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
