// ExportToWord.js
import React from 'react';
import { Document, Packer, Paragraph, TextRun } from 'docx';
import { saveAs } from 'file-saver';

const ExportToWord = ({ selectedEmployees }) => {
    const handleExport = () => {
        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const doc = new Document();

        // Добавляем заголовок
        doc.addSection({
            children: [
                new Paragraph({
                    children: [
                        new TextRun("Список сотрудников"),
                    ],
                }),
            ],
        });

        // Добавляем данные сотрудников
        selectedEmployees.forEach(employee => {
            doc.addSection({
                children: [
                    new Paragraph({
                        children: [
                            new TextRun(`${employee.first_name} ${employee.last_name} - ${employee.job_type} - ${employee.department}`),
                        ],
                    }),
                ],
            });
        });

        // Сохраняем документ
        Packer.toBlob(doc).then(blob => {
            saveAs(blob, 'employees.docx');
        });
    };

    return (
        <button onClick={handleExport}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
