import React, { useState } from 'react';
import './FilterComp.css'; // Импортируем стили

const FilterComp = ({ employees, jobTypes, departments }) => {
    const [filteredEmployees, setFilteredEmployees] = useState(employees);
    const [selectedJobTypes, setSelectedJobTypes] = useState([]);
    const [selectedDepartments, setSelectedDepartments] = useState([]);
    const [isNoneSelected, setIsNoneSelected] = useState(false); // Состояние для флажка "None"

    // Новые состояния для поисковых строк
    const [searchLastName, setSearchLastName] = useState('');
    const [searchJobType, setSearchJobType] = useState('');
    const [searchDepartment, setSearchDepartment] = useState('');

    const handleJobTypeChange = (e) => {
        const { value, checked } = e.target;
        setSelectedJobTypes(prevState => 
            checked ? [...prevState, value] : prevState.filter(id => id !== value)
        );
    };

    const handleDepartmentChange = (e) => {
        const { value, checked } = e.target;
        setSelectedDepartments(prevState => 
            checked ? [...prevState, value] : prevState.filter(id => id !== value)
        );
    };

    const handleNoneChange = (e) => {
        const { checked } = e.target;
        setIsNoneSelected(checked);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (isNoneSelected) {
            setFilteredEmployees([]);
        } else {
            const filtered = employees.filter(emp => {
                const matchesJobType = selectedJobTypes.length === 0 || selectedJobTypes.includes(emp.job_type.toString());
                const matchesDepartment = selectedDepartments.length === 0 || selectedDepartments.includes(emp.department.toString());

                // Поиск по фамилии
                const matchesLastName = searchLastName === '' || emp.last_name.toLowerCase().includes(searchLastName.toLowerCase());

                // Поиск по типу работы
                const jobTypeName = jobTypes.find(job => job.id === emp.job_type)?.name || '';
                const matchesJobTypeSearch = searchJobType === '' || jobTypeName.toLowerCase().includes(searchJobType.toLowerCase());

                // Поиск по отделу
                const departmentName = departments.find(dept => dept.id === emp.department)?.name || '';
                const matchesDepartmentSearch = searchDepartment === '' || departmentName.toLowerCase().includes(searchDepartment.toLowerCase());

                return matchesJobType && matchesDepartment && matchesLastName && matchesJobTypeSearch && matchesDepartmentSearch;
            });

            setFilteredEmployees(filtered);
        }
    };

    return (
        <div className="filter-comp">
            <form onSubmit={handleSubmit}>
                <h3>Filter Employees</h3>

                <div className="search-group">
                    <input
                        type="text"
                        placeholder="Search by Last Name"
                        value={searchLastName}
                        onChange={(e) => setSearchLastName(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Search by Job Type"
                        value={searchJobType}
                        onChange={(e) => setSearchJobType(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Search by Department"
                        value={searchDepartment}
                        onChange={(e) => setSearchDepartment(e.target.value)}
                    />
                </div>

                <div className="checkbox-group">
                    <div>
                        <h4>Job Types</h4>
                        {jobTypes.map(jobType => (
                            <div key={jobType.id}>
                                <input
                                    type="checkbox"
                                    id={`jobType-${jobType.id}`}
                                    value={jobType.id}
                                    onChange={handleJobTypeChange}
                                />
                                <label htmlFor={`jobType-${jobType.id}`}>{jobType.name}</label>
                            </div>
                        ))}
                    </div>

                    <div>
                        <h4>Departments</h4>
                        {departments.map(dept => (
                            <div key={dept.id}>
                                <input
                                    type="checkbox"
                                    id={`dept-${dept.id}`}
                                    value={dept.id}
                                    onChange={handleDepartmentChange}
                                />
                                <label htmlFor={`dept-${dept.id}`}>{dept.name}</label>
                            </div>
                        ))}
                    </div>

                    <div>
                        <input
                            type="checkbox"
                            id="resetFilters"
                            checked={isNoneSelected}
                            onChange={handleNoneChange}
                        />
                        <label htmlFor="resetFilters">None (Clear Filters)</label>
                    </div>
                </div>

                <button type="submit">Apply Filters</button>
            </form>

            {/* Таблица сотрудников */}
            <EmployeeTable 
                employees={filteredEmployees} 
                jobTypes={jobTypes} 
                departments={departments} 
            />
        </div>
    );
};

// Компонент таблицы для отображения сотрудников
const EmployeeTable = ({ employees, jobTypes, departments }) => {
    return (
        <table className="styled-table">
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Salary</th>
                    <th>Job Type</th>
                    <th>Department</th>
                </tr>
            </thead>
            <tbody>
                {employees.map(emp => (
                    <tr key={emp.id}>
                        <td>{emp.first_name}</td>
                        <td>{emp.last_name}</td>
                        <td>{emp.salary}</td>
                        <td>{jobTypes.find(job => job.id === emp.job_type)?.name || 'Unknown Job Type'}</td>
                        <td>{departments.find(dept => dept.id === emp.department)?.name || 'Unknown Department'}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default FilterComp;
