import React from 'react';
import { Document, Packer, Paragraph, TextRun } from 'docx';
import fileDownload from 'js-file-download';

const ExportToWord = ({ selectedEmployees }) => {
    const generateDocument = async () => {
        const doc = new Document({
            sections: [
                {
                    properties: {},
                    children: selectedEmployees.map(emp => (
                        new Paragraph({
                            children: [
                                new TextRun(`First Name: ${emp.first_name}`),
                                new TextRun({ text: `\nLast Name: ${emp.last_name}`, break: 1 }),
                                new TextRun({ text: `\nJob Type: ${emp.job_type_name}`, break: 1 }),
                                new TextRun({ text: `\nDepartment: ${emp.department_name}`, break: 1 }),
                                new TextRun({ text: `\nSalary: ${emp.salary}`, break: 1 }),
                                new TextRun({ text: `\n------------------------------------------`, break: 1 }),
                            ],
                        })
                    )),
                },
            ],
        });

        const blob = await Packer.toBlob(doc);
        fileDownload(blob, 'employees.docx');
    };

    return (
        <div>
            <button onClick={generateDocument}>
                Download Word Document
            </button>
        </div>
    );
};

export default ExportToWord;
