// ExportToWord.js
import React from 'react';
import { Document, Packer, Paragraph, TextRun } from 'docx';
import { saveAs } from 'file-saver';
import './FilterComp.css';
const ExportToWord = ({ selectedEmployees }) => {
    const handleExport = () => {
        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const doc = new Document({
            sections: [{
                properties: {},
                children: [
                    new Paragraph({
                        text: "Список сотрудников",
                        heading: 'Heading1', // Задайте заголовок
                    }),
                    ...selectedEmployees.map(employee => (
                        new Paragraph({
                            text: `${employee.first_name} ${employee.last_name} - ${employee.job_type} - ${employee.department}`
                        })
                    )),
                ],
            }],
        });

        Packer.toBlob(doc).then(blob => {
            saveAs(blob, 'employees.docx');
        });
    };

    return (
        <button onClick={handleExport}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
