// ExportToExcel.js
import React from 'react';
import * as XLSX from 'xlsx';
import { utils, writeFile } from 'xlsx';

const ExportToExcel = ({ selectedEmployees }) => {
    const handleExport = () => {
        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const worksheet = utils.json_to_sheet(selectedEmployees);
        const workbook = utils.book_new();
        utils.book_append_sheet(workbook, worksheet, 'Employees');

        writeFile(workbook, 'employees.xlsx');
    };

    return (
        <button onClick={handleExport}>
            Экспорт в Excel
        </button>
    );
};

export default ExportToExcel;
