import React, { useEffect, useState } from 'react';
import { Document, Packer, Paragraph, Table, TableRow, TableCell, WidthType } from 'docx';
import { saveAs } from 'file-saver';

const ExportToWord = ({ selectedEmployees }) => {
    const [jobTypes, setJobTypes] = useState([]);
    const [departments, setDepartments] = useState([]);

    useEffect(() => {
        const fetchJobTypes = async () => {
            try {
                const response = await fetch('/api/job-types');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                console.log('Fetched job types:', data);
                setJobTypes(data);
            } catch (error) {
                console.error('Error fetching job types:', error);
            }
        };

        const fetchDepartments = async () => {
            try {
                const response = await fetch('/api/departments');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                console.log('Fetched departments:', data);
                setDepartments(data);
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        fetchJobTypes();
        fetchDepartments();
    }, []);

    const handleExport = () => {
        console.log('Selected Employees:', selectedEmployees);
        console.log('Job Types:', jobTypes);
        console.log('Departments:', departments);

        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const doc = new Document({
            sections: [{
                properties: {},
                children: [
                    new Paragraph({
                        text: 'Список сотрудников',
                        heading: 'Title',
                    }),
                    new Paragraph({
                        text: 'В данном документе представлен список сотрудников с их должностями и отделами.',
                        spacing: { after: 200 },
                    }),
                    new Table({
                        rows: [
                            new TableRow({
                                children: [
                                    new TableCell({
                                        children: [new Paragraph('Имя')],
                                        width: { size: 20, type: WidthType.PERCENTAGE }
                                    }),
                                    new TableCell({
                                        children: [new Paragraph('Фамилия')],
                                        width: { size: 20, type: WidthType.PERCENTAGE }
                                    }),
                                    new TableCell({
                                        children: [new Paragraph('Зарплата')],
                                        width: { size: 20, type: WidthType.PERCENTAGE }
                                    }),
                                    new TableCell({
                                        children: [new Paragraph('Тип работы')],
                                        width: { size: 20, type: WidthType.PERCENTAGE }
                                    }),
                                    new TableCell({
                                        children: [new Paragraph('Отдел')],
                                        width: { size: 20, type: WidthType.PERCENTAGE }
                                    }),
                                ]
                            }),
                            ...selectedEmployees.map(emp => 
                                new TableRow({
                                    children: [
                                        new TableCell({
                                            children: [new Paragraph(emp.first_name || '')]
                                        }),
                                        new TableCell({
                                            children: [new Paragraph(emp.last_name || '')]
                                        }),
                                        new TableCell({
                                            children: [new Paragraph(emp.salary?.toString() || '')]
                                        }),
                                        new TableCell({
                                            children: [
                                                new Paragraph(
                                                    jobTypes.find(job => job.id === emp.job_type)?.name || 'Unknown Job Type'
                                                )
                                            ]
                                        }),
                                        new TableCell({
                                            children: [
                                                new Paragraph(
                                                    departments.find(dept => dept.id === emp.department)?.name || 'Unknown Department'
                                                )
                                            ]
                                        }),
                                    ]
                                })
                            )
                        ]
                    }),
                    new Paragraph({
                        text: 'Дата генерации документа: ' + new Date().toLocaleDateString(),
                        spacing: { after: 200 },
                    }),
                ],
            }],
        });

        // Генерация файла и скачивание
        Packer.toBlob(doc).then(blob => {
            saveAs(blob, 'employees.docx');
        });
    };

    return (
        <button className="export-button" onClick={handleExport} disabled={selectedEmployees.length === 0}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
