import React from 'react';
import { Document, Packer, Paragraph, Table, TableRow, TableCell, WidthType } from 'docx';
import { saveAs } from 'file-saver';

const ExportToWord = ({ selectedEmployees, jobTypes = [], departments = [] }) => {
    const handleExport = () => {
        if (selectedEmployees.length === 0) {
            alert("Нет выбранных сотрудников для экспорта.");
            return;
        }

        const doc = new Document({
            sections: [
                {
                    properties: {},
                    children: [
                        new Paragraph({
                            text: 'Employee List',
                            heading: 'Title',
                        }),
                        new Table({
                            rows: [
                                new TableRow({
                                    children: [
                                        new TableCell({
                                            children: [new Paragraph('First Name')],
                                            width: { size: 25, type: WidthType.PERCENTAGE }
                                        }),
                                        new TableCell({
                                            children: [new Paragraph('Last Name')],
                                            width: { size: 25, type: WidthType.PERCENTAGE }
                                        }),
                                        new TableCell({
                                            children: [new Paragraph('Salary')],
                                            width: { size: 25, type: WidthType.PERCENTAGE }
                                        }),
                                        new TableCell({
                                            children: [new Paragraph('Job Type')],
                                            width: { size: 25, type: WidthType.PERCENTAGE }
                                        }),
                                        new TableCell({
                                            children: [new Paragraph('Department')],
                                            width: { size: 25, type: WidthType.PERCENTAGE }
                                        }),
                                    ]
                                }),
                                ...selectedEmployees.map(emp =>
                                    new TableRow({
                                        children: [
                                            new TableCell({
                                                children: [new Paragraph(emp.first_name || '')]
                                            }),
                                            new TableCell({
                                                children: [new Paragraph(emp.last_name || '')]
                                            }),
                                            new TableCell({
                                                children: [new Paragraph(emp.salary?.toString() || '')]
                                            }),
                                            new TableCell({
                                                children: [
                                                    new Paragraph(
                                                        jobTypes.find(job => job.id === emp.job_type)?.name || 'Unknown Job Type'
                                                    )
                                                ]
                                            }),
                                            new TableCell({
                                                children: [
                                                    new Paragraph(
                                                        departments.find(dept => dept.id === emp.department)?.name || 'Unknown Department'
                                                    )
                                                ]
                                            }),
                                        ]
                                    })
                                )
                            ]
                        })
                    ]
                }
            ]
        });

        Packer.toBlob(doc).then(blob => {
            saveAs(blob, 'selected_employees.docx');
        });
    };

    return (
        <button className="export-button" onClick={handleExport} disabled={selectedEmployees.length === 0}>
            Экспорт в Word
        </button>
    );
};

export default ExportToWord;
